:root {
  --main_black: #131313;
  --second__black:#363636;
  --main_white: #d4d4d4;
  --main-theme: #049b87;
}

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
}

/* ######### GLOBAL ################## */
.btn {
  height: 50px;
  width: 170px;
  border: none;
  background: transparent;
  color: var(--main_white);
  background: var(--main_black);
}
.btn:hover {
  background: var(--second__black);
  cursor: pointer;
}

/* ############## HEADER ################### */
.app-header {
  max-width: 1200px;
  margin: 0 auto;
  display: flex;
  justify-content: space-between;
  align-items: center;
  height: 60px;
  padding: 0 20px;
}

.header__list {
  display: flex;
  list-style-type: none;
}

.header__item {
  margin: 0 10px;
}

.header__item:last-child {
  margin-right: 0;
}

.header__link {
  text-decoration: none;
  color: var(--main_black);
}
.header__link:hover {
  color: var(--second__black);
}

/* ############ MAIN ############# */
.app-main {
  font-size: 16px;
}

.intro {
  display: flex;
  justify-content: center;
  align-items: flex-start;
  height: calc(100vh - 60px);
  background-image: linear-gradient(to bottom, rgba(255, 255, 255, 0.178), rgba(221, 221, 221, 0.993)), url("1bb5be20e9ebd20c336232485ce280e9.jpg");
  background-position: top center;
  background-size: cover;
  font-size: 25px;
}

.intro__header {
  margin-top: 20%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
}
.intro__header h1 {
  font-size: 4em;
  padding: 10px 60px;
  text-align: center;
  color: var(--main_black);
}
.intro__header p {
  text-align: center;
  color: var(--second__black);
}
.intro__header .btn--intro {
  margin-top: 5%;
}

.products {
  max-width: 1800px;
  margin: 0 auto;
  padding-left: 20px;
  padding-right: 20px;
  padding-bottom: 60px;
}

.products__header {
  display: flex;
  justify-content: center;
  align-items: center;
}
.products__header h1 {
  font-size: 3em;
  padding: 40px;
}

.products__container {
  max-width: 100%;
  height: 100%;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  grid-template-rows: auto;
  grid-column-gap: 20px;
  grid-row-gap: 20px;
}

.products__image {
  overflow: hidden;
  position: relative;
}
.products__image img {
  object-fit: cover;
  margin: 0;
  opacity: 0.8;
  transition: opacity 0.5s ease-in-out;
}
.products__image img:hover {
  opacity: 1;
}
.products__image i {
  position: absolute;
  bottom: 5px;
  right: 0px;
  background: var(--main-theme);
  color: var(--main_white);
  padding: 20px;
  transform: translateX(101%);
  transition: transform 0.3s ease-in-out;
  cursor: pointer;
  font-size: 30px;
}
.products__image:hover i {
  transform: translateX(0%);
}

.products__title {
  margin-top: 10px;
}

.products__price {
  color: var(--main-theme);
  margin-top: 10px;
}

/* ############# FOOTER ############# */
.app-footer {
  height: 300px;
  margin: 0 auto;
  background: var(--second__black);
}

.footer__copy {
  margin: 0 auto;
  max-width: 1200px;
  padding: 100px 20px;
  color: var(--main_white);
}
